mapper.select:
select
from t_blog
and title like concat('%',#{title, jdbcType=VARCHAR},'%')
and title like concat('%',#{title, jdbcType=VARCHAR},'%') or description like concat('%',#{description, jdbcType=VARCHAR},'%')
and type_Id = #{typeId, jdbcType=INTEGER}
and recommend = #{recommend, jdbcType=TINYINT}

mapper.update:
update t_blog set title=#{title}, content=#{content}, description=#{description}, firstPic=#{firstPic}, flag=#{flag},
views=#{views}, appreciation=#{appreciation}, copyright=#{copyright}, commentAble=#{commentAble},
publish=#{publish}, recommend=#{recommend}, createTime=#{createTime}, updateTime=#{updateTime}, type_id=#{type.id}
where Id=#{id}
